import React from "react";
import ReactDOM from "react-dom";
import Home from "./Home";
import Content from "./Home/Components/Content";
import Nav from "./Home/Components/Nav";

class App extends React.Component {
  state = {
    banner: "Welcome Message",
    isMoreHidder: true,
  };
  changeHeader = () => {
    this.setState({ banner: "Have a Good Time!" });
  };

  moreHidder = () => {
    // this.setState( {isBoxHidden: false} )
    this.setState({ isMoreHidden: !this.state.isBoxHidden });
  };

  render() {
    return (
      <div>
        <Nav />
        <Content
          title={this.state.banner}
          onClick={this.changeHeader}
          onClick={this.displayBox}
        />
      </div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
