import React from "react";
import ReactDOM from "react-dom";
import Home from "./Home";
import Content from "./Home/Components/Content";
import Nav from "./Home/Components/Nav";
import Seemore from "./Home/Components/Seemore";
import Footer from "./Home/Components/footer";
import SideMenu from "./Home/Components/SideMenu";

class App extends React.Component {
  state = {
    banner: "Welcome Message",
    isMoreHidder: true,
    isSideMenuHidden: true,
  };
  changeHeader = () => {
    this.setState({ banner: "Have a Good Time!" });
  };

  moreHidder = () => {
    // this.setState( {isBoxHidden: false} )
    this.setState({ isMoreHidden: !this.state.isMoreHidden });
  };

  showSideMenu = () => {
    this.setState({ isSideMenuHidden: !this.state.isSideMenuHidden });
  };
  // <NavBar onClick = {this.displayNavItems} hideNavItems = {this.state.hideNavItems} />
  render() {
    return (
      <div>
        <SideMenu conClick={this.showSideMenu} />
        <Nav />
        <Content
          title={this.state.banner}
          onClick={this.changeHeader}
          onClick={this.moreHidder}
        />
        {this.state.isMoreHidden && <Seemore />}
        {/* <Seemore onClick={this.moreHidder} /> */}
        <Footer />
      </div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById("root")
);
